import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title GasCreditVault
 * @dev A vault contract that allows users to deposit tokens and receive credits for gas payments
 * Features:
 * - Multi-token support with stablecoin and price feed integration
 * - Credit-based system for gas payment abstraction
 * - Relayer-based consumption model
 * - Owner withdrawal of consumed credits
 * - Emergency pause functionality
 * - Upgradeable contract pattern
 */
contract GasCreditVault is Initializable, OwnableUpgradeable, UUPSUpgradeable  {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    /**
     * @dev Token information structure
     * @param priceFeed Chainlink price feed interface for non-stablecoin tokens
     * @param isStablecoin Whether the token is a stablecoin (1:1 USD)
     */
    struct TokenInfo {
        AggregatorV3Interface priceFeed;
        bool isStablecoin;
    }

    // Events
    event TokenWhitelisted(address indexed token, address priceFeed);
    event TokenRemoved(address indexed token);
    event Deposited(address indexed user, address indexed token, uint256 amount, uint256 credited);
    event Withdrawn(address indexed user, address indexed token, uint256 amount, uint256 credited);
    event CreditsConsumed(address indexed user, uint256 usdValue, uint256 creditCost);
    event CreditTransfer(address indexed sender, address indexed receiver, uint256 creditAmount);
    event OwnerWithdrawn(address indexed token, uint256 amount, uint256 creditedConsumed);
    event RelayerAdded(address indexed relayer);
    event RelayerRemoved(address indexed relayer);
    event EmergencyWithdrawn(address indexed to, uint256 amount);
    event Paused();
    event Unpaused();

    // State variables
    EnumerableSet.AddressSet private whitelistedTokens;
    EnumerableSet.AddressSet private relayers;

    mapping(address => TokenInfo) public tokenInfo;
    mapping(address => uint256) public credits;
    mapping(address => mapping(address => uint256)) public creditsInToken;

    uint256 public totalConsumedCreditsWithdrawn;
    uint256 public totalConsumedCredits;

    uint256 public minimumConsume;
    uint8 public constant creditDecimals = 18;
    uint256 public constant PRICE_FEED_TIMEOUT = 3600; // 1 hour for stale price protection

    bool public paused;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializes the contract with default settings
     * @notice This function can only be called once during deployment
     * Sets up ownership, upgradeability, and default minimum consume amount
     */
    function initialize() public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        minimumConsume = 0.05 ether;
    }

    // Modifiers

    /**
     * @dev Modifier to restrict function access to whitelisted relayers only
     */
    modifier onlyRelayer() {
        require(relayers.contains(msg.sender), "Caller not whitelisted relayers");
        _;
    }

    /**
     * @dev Modifier to ensure the token is a stablecoin
     * @param token The token address to check
     */
    modifier onlyStablecoin(address token) {
        require(tokenInfo[token].isStablecoin, "Token not stablecoin");
        _;
    }

    /**
     * @dev Modifier to prevent function execution when contract is paused
     */
    modifier whenNotPaused() {
        require(!paused, "Paused");
        _;
    }

    /**
     * @dev Modifier to ensure function execution only when contract is paused
     */
    modifier whenPaused() {
        require(paused, "Unpaused");
        _;
    }
    // Owner functions ==============================================

    /**
     * @dev Adds a new relayer to the whitelist
     * @param relayer The address of the relayer to add
     * @notice Only owner can call this function
     * @notice Relayer address must not be zero and not already whitelisted
     */
    function addWhitelistedRelayer(address relayer) external onlyOwner {
        require(relayer != address(0), "Invalid address");
        require(!relayers.contains(relayer), "Relayer already whitelisted");

        relayers.add(relayer);

        emit RelayerAdded(relayer);
    }

    /**
     * @dev Removes a relayer from the whitelist
     * @param relayer The address of the relayer to remove
     * @notice Only owner can call this function
     * @notice Relayer must be currently whitelisted
     */
    function removeWhitelistedRelayer(address relayer) external onlyOwner {
        require(relayers.contains(relayer), "Relayer not whitelisted");

        relayers.remove(relayer);

        emit RelayerRemoved(relayer);
    }

    /**
     * @dev Adds a token to the whitelist with price feed configuration
     * @param token The token contract address
     * @param priceFeed The Chainlink price feed address (can be zero for stablecoins)
     * @param isStablecoin Whether the token is a stablecoin (1:1 USD value)
     * @notice Only owner can call this function
     * @notice Token must not already be whitelisted
     * @notice Non-stablecoins must have a valid price feed
     */
    function whitelistToken(
        address token,
        address priceFeed,
        bool isStablecoin
    ) external onlyOwner {
        require(token != address(0), "Invalid token address");
        require(!whitelistedTokens.contains(token), "Token already whitelisted");
        require(isStablecoin || priceFeed != address(0), "Invalid price feed");

        whitelistedTokens.add(token);
        tokenInfo[token] = TokenInfo({
            priceFeed: AggregatorV3Interface(priceFeed),
            isStablecoin: isStablecoin
        });

        emit TokenWhitelisted(token, priceFeed);
    }

    /**
     * @dev Removes a token from the whitelist
     * @param token The token contract address to remove
     * @notice Only owner can call this function
     * @notice Token must have zero balance in the contract
     * @notice Token must be currently whitelisted
     */
    function removeToken(address token) external onlyOwner {
        require(IERC20(token).balanceOf(address(this)) == 0, "None Zero Balance");
        require(whitelistedTokens.contains(token), "Token not whitelisted");
        whitelistedTokens.remove(token);
        delete tokenInfo[token];

        emit TokenRemoved(token);
    }

    /**
     * @dev Pauses the contract, preventing deposits, withdrawals, and transfers
     * @notice Only owner can call this function
     * @notice Contract must not already be paused
     */
    function pause() external onlyOwner {
        require(!paused, "Already paused");
        paused = true;
        emit Paused();
    }

    /**
     * @dev Unpauses the contract, allowing normal operations
     * @notice Only owner can call this function
     * @notice Contract must be currently paused
     */
    function unpause() external onlyOwner {
        require(paused, "Not paused");
        paused = false;
        emit Unpaused();
    }

    /**
     * @dev Emergency function to withdraw all tokens to owner
     * @notice Only owner can call this function
     * @notice Should only be used in emergency situations
     * @notice Withdraws all balances of whitelisted tokens
     */
    function emergencyWithdraw() external onlyOwner {
        address[] memory tokens = whitelistedTokens.values();
        for (uint256 i = 0; i < tokens.length; ++i) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(owner(), balance);
                emit EmergencyWithdrawn(token, balance);
            }
        }        
    }

    /**
     * @dev Sets the minimum consumption amount
     * @param _minimum The new minimum consumption amount in credit decimals
     * @notice Only owner can call this function
     * @notice Minimum must be greater than zero
     */
    function setMinimumConsume(uint256 _minimum) external onlyOwner {
        require(_minimum > 0, "Minimum must be > 0");
        minimumConsume = _minimum;
    }

    // User functions ==============================================

    /**
     * @dev Allows users to deposit tokens and receive credits
     * @param token The token contract address to deposit
     * @param amount The amount of tokens to deposit
     * @notice Token must be whitelisted and amount must be greater than zero
     * @notice Contract must not be paused
     * @notice Credits are calculated based on token price and decimals
     */
    function deposit(address token, uint256 amount) external whenNotPaused {
        require(whitelistedTokens.contains(token), "Token not whitelisted");
        require(amount > 0, "Amount must be > 0");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        uint256 creditedAmount = calculateCreditValue(token, amount);

        credits[msg.sender] += creditedAmount;
        creditsInToken[msg.sender][token] += creditedAmount;

        emit Deposited(msg.sender, token, amount, creditedAmount);
    }

    /**
     * @dev Allows users to withdraw stablecoins using their credits
     * @param token The stablecoin contract address to withdraw
     * @param creditAmount The amount of credits to convert to tokens
     * @notice Only works with stablecoins
     * @notice User must have sufficient credits in the specified token
     * @notice Contract must not be paused
     * @notice Vault must have sufficient token balance
     */
    function withdraw(address token, uint256 creditAmount) external whenNotPaused onlyStablecoin(token) {
        require(creditsInToken[msg.sender][token] >= creditAmount, "Insufficient token balance");

        uint256 tokenAmount = calculateTokenValue(token, creditAmount);
        require(IERC20(token).balanceOf(address(this)) >= tokenAmount, "Insufficient token in Vault");

        // Update credits
        credits[msg.sender] -= creditAmount;
        creditsInToken[msg.sender][token] -= creditAmount;

        IERC20(token).safeTransfer(msg.sender, tokenAmount);

        emit Withdrawn(msg.sender, token, tokenAmount, creditAmount);
    }

    // Consumption function ====================================

    /**
     * @dev Allows relayers to consume user credits for gas payment
     * @param user The user whose credits will be consumed
     * @param usdValue The USD value of gas consumed
     * @notice Only whitelisted relayers can call this function
     * @notice Uses minimum consume amount if usdValue is below threshold
     * @notice Deducts credits proportionally from user's token positions
     */
    function consumeCredit(address user, uint256 usdValue) external onlyRelayer {
        require(user != address(0), "Invalid user address");
        require(usdValue > 0, "Value must be > 0");

        uint256 creditCost = usdValue > minimumConsume ? usdValue : minimumConsume;

        require(credits[user] >= creditCost, "Insufficient credits");

        uint256 remaining = creditCost;

        // Iterate over whitelisted tokens and deduct credited and deposited accordingly
        address[] memory tokens = whitelistedTokens.values();
        for (uint256 i = 0; i < tokens.length && remaining > 0; ++i) {
            address token = tokens[i];
            uint256 userCredited = creditsInToken[user][token];

            if (userCredited == 0) continue;

            uint256 deduction = userCredited >= remaining ? remaining : userCredited;

            creditsInToken[user][token] -= deduction;

            remaining -= deduction;
        }

        credits[user] -= creditCost;
        totalConsumedCredits += creditCost;

        emit CreditsConsumed(user, usdValue, creditCost);
    }

    /**
     * @dev Allows users to transfer credits to another address
     * @param receiver The address to receive the credits
     * @param credit The amount of credits to transfer
     * @notice Contract must not be paused
     * @notice Credit amount must be above minimum and sender must have sufficient balance
     * @notice Credits are transferred proportionally from sender's token positions to receiver
     */
    function transferCredit(address receiver, uint256 credit) external whenNotPaused {
        address sender = msg.sender;
        require(receiver != address(0), "Invalid receiver address");
        require(credits[sender] >= credit, 'Invalid amount');

        uint256 remaining = credit;
        address[] memory tokens = whitelistedTokens.values();
        for (uint256 i = 0; i < tokens.length && remaining > 0; ++i) {
            address token = tokens[i];
            uint256 userCredited = creditsInToken[sender][token];

            if (userCredited == 0) continue;

            uint256 deduction = userCredited >= remaining ? remaining : userCredited;

            creditsInToken[sender][token] -= deduction;
            creditsInToken[receiver][token] += deduction;
            remaining -= deduction;
        }

        credits[receiver] += credit;
        credits[sender] -= credit;
        emit CreditTransfer(sender, receiver, credit);
    }

        /**
     * @dev Allows owner to withdraw tokens corresponding to consumed credits
     * @notice Only owner can call this function
     * @notice Only withdraws new consumed credits since last withdrawal
     * @notice Proportionally withdraws from all token balances based on consumed credits
     */
    function withdrawConsumedCredits() external onlyOwner {
        require(totalConsumedCredits > totalConsumedCreditsWithdrawn, "No new credits to withdraw");

        uint256 deltaCredits = totalConsumedCredits - totalConsumedCreditsWithdrawn;
        totalConsumedCreditsWithdrawn = totalConsumedCredits; // Fix: Update the withdrawn amount

        address[] memory tokens = whitelistedTokens.values();

        // Calculate and withdraw tokens corresponding to consumed credits
        for (uint256 i = 0; i < tokens.length; ++i) {
            address token = tokens[i];

            // Get the token balance for the contract
            uint256 contractBalance = IERC20(token).balanceOf(address(this));
            if (contractBalance == 0) continue;

            // Calculate the credit value for the token balance in terms of consumed credits
            uint256 tokenValueInCredits = calculateCreditValue(token, contractBalance);

            // Withdraw the corresponding amount of tokens based on the consumed credits
            uint256 tokenAmount = (deltaCredits * contractBalance) / tokenValueInCredits;

            // Ensure we don't withdraw more than the available balance
            if (tokenAmount > contractBalance) {
                tokenAmount = contractBalance;
            }

            // If tokenAmount is greater than zero, withdraw it to the owner
            if (tokenAmount > 0) {
                IERC20(token).safeTransfer(owner(), tokenAmount);
                emit OwnerWithdrawn(token, tokenAmount, deltaCredits);
            }
        }
    }

    // Price calculation functions ================================

    /**
     * @dev Calculates the credit value for a given token amount
     * @param token The token contract address
     * @param amount The amount of tokens
     * @return The equivalent credit value in credit decimals
     * @notice For stablecoins, assumes 1:1 USD ratio
     * @notice For other tokens, uses Chainlink price feed
     */
    function calculateCreditValue(address token, uint256 amount) internal view returns (uint256) {
        TokenInfo memory info = tokenInfo[token];
        uint8 tokenDecimals = IERC20Metadata(token).decimals();

        if (info.isStablecoin) {
            return convertDecimals(amount, tokenDecimals, creditDecimals);
        }

        (uint80 roundId, int256 price, , uint256 updatedAt, uint80 answeredInRound) = info.priceFeed.latestRoundData();

        // Price feed validation
        require(price > 0, "Invalid price from feed");
        require(updatedAt > 0, "Invalid timestamp from feed");
        require(block.timestamp - updatedAt <= PRICE_FEED_TIMEOUT, "Price feed too stale");
        require(answeredInRound >= roundId, "Price feed round incomplete");

        uint8 _decimals = tokenDecimals + info.priceFeed.decimals();

        // Formula: (amount * price) / (10^(tokenDecimals + priceFeedDecimals - creditDecimals))
        return convertDecimals(amount * uint256(price), _decimals, creditDecimals);
    }

    /**
     * @dev Calculates the token amount for a given credit value
     * @param token The token contract address
     * @param creditAmount The amount of credits
     * @return The equivalent token amount in token decimals
     * @notice For stablecoins, assumes 1:1 USD ratio
     * @notice For other tokens, uses Chainlink price feed
     */
    function calculateTokenValue(address token, uint256 creditAmount) internal view returns (uint256) {
        TokenInfo memory info = tokenInfo[token];
        uint8 tokenDecimals = IERC20Metadata(token).decimals();

        if (info.isStablecoin) {
            return convertDecimals(creditAmount, creditDecimals, tokenDecimals);
        }

        (uint80 roundId, int256 price, , uint256 updatedAt, uint80 answeredInRound) = info.priceFeed.latestRoundData();

        // Price feed validation
        require(price > 0, "Invalid price from feed");
        require(updatedAt > 0, "Invalid timestamp from feed");
        require(block.timestamp - updatedAt <= PRICE_FEED_TIMEOUT, "Price feed too stale");
        require(answeredInRound >= roundId, "Price feed round incomplete");

        uint8 priceFeedDecimals = info.priceFeed.decimals();

        // Fix precision: multiply first, then divide
        // Formula: (creditAmount * 10^(tokenDecimals + priceFeedDecimals)) / (price * 10^creditDecimals)
        uint256 numerator = convertDecimals(creditAmount, creditDecimals, creditDecimals + tokenDecimals + priceFeedDecimals);
        return numerator / uint256(price);
    }

    /**
     * @dev Converts amounts between different decimal precisions
     * @param amount The amount to convert
     * @param from The source decimal precision
     * @param to The target decimal precision
     * @return The converted amount
     * @notice Uses unchecked arithmetic for gas optimization
     */
    function convertDecimals(uint256 amount, uint8 from, uint8 to) internal pure returns (uint256) {
        unchecked {
            return from > to ? 
                amount / (10 ** (from - to)) :
                amount * (10 ** (to - from));
        }
    }

    /**
     * @dev Authorizes contract upgrades (UUPS pattern)
     * @param newImplementation The address of the new implementation
     * @notice Only owner can authorize upgrades
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}


    // View functions =============================================

    /**
     * @dev Returns the list of all whitelisted token addresses
     * @return Array of whitelisted token addresses
     */
    function getWhitelistedTokens() external view returns (address[] memory) {
        return whitelistedTokens.values();
    }

    /**
     * @dev Returns the list of all whitelisted relayer addresses
     * @return Array of whitelisted relayer addresses
     */
    function getWhitelistedRelayers() external view returns (address[] memory) {
        return relayers.values();
    }

    /**
     * @dev Calculates the credit value for a given token amount (external view)
     * @param token The token contract address
     * @param amount The amount of tokens
     * @return The equivalent credit value
     */
    function getCreditValue(address token, uint256 amount) external view returns (uint256) {
        return calculateCreditValue(token, amount);
    }

    /**
     * @dev Calculates the token amount for a given credit value (external view)
     * @param token The token contract address
     * @param creditAmount The amount of credits
     * @return The equivalent token amount
     */
    function getTokenValue(address token, uint256 creditAmount) external view returns (uint256) {
        return calculateTokenValue(token, creditAmount);
    }

    /**
     * @dev Checks if a token is whitelisted
     * @param token The token contract address to check
     * @return Whether the token is whitelisted
     */
    function isTokenWhitelisted(address token) external view returns (bool) {
        return whitelistedTokens.contains(token);
    }

    /**
     * @dev Checks if an address is a whitelisted relayer
     * @param relayer The address to check
     * @return Whether the address is a whitelisted relayer
     */
    function isRelayerWhitelisted(address relayer) external view returns (bool) {
        return relayers.contains(relayer);
    }
}

contracts/KOLAllocation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract KOLAllocation is Ownable {
    struct Member {
        string name;
        uint256 balance;
        uint256 startTime;
        uint256 withdrawn;
    }

    IERC20 public immutable token;

    uint256 public constant VESTING_DURATION = 180 days;
    uint256 public constant CLIFF_DURATION = 30 days;
    uint256 private constant NOT_FOUND = type(uint256).max;

    address[] public wallets;
    Member[] public membersInfo;

    uint256 public totalAllocated;
    uint256 public totalWithdrawn;

    constructor(address _token, address _owner) Ownable(_owner) {
        token = IERC20(_token);
    }

    function allocateTokens(
        address[] calldata _wallets,
        string[] calldata names,
        uint256[] calldata amounts
    ) external onlyOwner {
        require(
            _wallets.length == names.length && names.length == amounts.length,
            "Mismatched input lengths"
        );

        for (uint256 i = 0; i < _wallets.length; ++i) {
            wallets.push(_wallets[i]);
            membersInfo.push(Member({
                name: names[i],
                balance: amounts[i],
                startTime: block.timestamp,
                withdrawn: 0
            }));
            totalAllocated += amounts[i];
        }
    }

    function withdraw() external {
        uint256 index = getMemberIndex(msg.sender);
        require(index != NOT_FOUND, "No Member wallet");

        Member storage member = membersInfo[index];
        uint256 withdrawable = calculateWithdrawable(member);
        require(withdrawable > 0, "Nothing to withdraw");

        member.withdrawn += withdrawable;
        totalWithdrawn += withdrawable;
        token.transfer(msg.sender, withdrawable);
    }

    function getWithdrawable() external view returns (uint256) {
        uint256 index = getMemberIndex(msg.sender);
        require(index != NOT_FOUND, "No Member wallet");
        return calculateWithdrawable(membersInfo[index]);
    }

    function calculateWithdrawable(Member memory member) internal view returns (uint256) {
        if (block.timestamp < member.startTime + CLIFF_DURATION) {
            return 0;
        }

        uint256 elapsed = block.timestamp - member.startTime;
        uint256 vested;

        if (elapsed >= VESTING_DURATION) {
            vested = member.balance;
        } else {
            // 20% after cliff, remaining linearly over remaining time
            uint256 initial = member.balance / 5;
            uint256 linearPart = (member.balance * 4 * (elapsed - CLIFF_DURATION)) / (VESTING_DURATION * 5);
            vested = initial + linearPart;
        }

        if (vested > member.balance) vested = member.balance;

        return vested - member.withdrawn;
    }

    function getMemberIndex(address wallet) internal view returns (uint256) {
        for (uint256 i = 0; i < wallets.length; ++i) {
            if (wallets[i] == wallet) return i;
        }
        return NOT_FOUND;
    }

    function getMemberCount() external view returns (uint256) {
        return wallets.length;
    }

    function getMember(address wallet) external view returns (Member memory) {
        uint256 index = getMemberIndex(wallet);
        require(index != NOT_FOUND, "No Member wallet");
        return membersInfo[index];
    }
}

contracts/MetaTxGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

/**
 * @title MetaTxGateway
 * @notice Gateway for executing gasless meta-transactions on any EVM chain
 * @dev Does not handle gas credits - relies on external relayer for credit management
 * @dev Only supports batch execution - single meta-transactions must be wrapped in a batch
 * @dev Upgradeable contract using UUPS pattern with pause functionality
 */
contract MetaTxGateway is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable, UUPSUpgradeable {
    using ECDSA for bytes32;

    // EIP-712 Domain Separator
    bytes32 private constant EIP712_DOMAIN_TYPEHASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    // EIP-712 MetaTransaction struct typehash
    bytes32 private constant META_TRANSACTION_STRUCT_TYPEHASH = keccak256(
        "MetaTransaction(address to,uint256 value,bytes data)"
    );

    // EIP-712 Main typehash for batch meta-transactions
    bytes32 private constant META_TRANSACTION_TYPEHASH = keccak256(
        "MetaTransactions(address from,MetaTransaction[] metaTxs,uint256 nonce,uint256 deadline)MetaTransaction(address to,uint256 value,bytes data)"
    );
    
    // Relayer management
    mapping(address => bool) public authorizedRelayers;
    
    // Nonce management for replay protection
    mapping(address => uint256) public nonces;

    // Mapping from batch transaction ID to log
    uint256 public nextBatchId;

    // Pause-related state variables
    struct PauseInfo {
        uint256 pausedAt;
        string pauseReason;
    }
    
    PauseInfo public pauseState;

    struct MetaTransaction {
        address to;        // Target contract to call
        uint256 value;     // ETH value to send (usually 0)
        bytes data;        // Function call data
    }

    event RelayerAuthorized(address indexed relayer, bool authorized);
    event MetaTransactionExecuted(
        address indexed relayer,
        address indexed user,
        address indexed target,
        uint256 value,
        bytes data,
        bool success
    );
    event NativeTokenUsed(
        uint256 indexed batchId,
        uint256 totalRequired,
        uint256 totalUsed,
        uint256 refunded
    );
    event PausedWithReason(string reason);
    event TokenRescued(address indexed token, address indexed to, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializes the contract with the initial owner
     * @notice This function can only be called once during deployment
     */
    function initialize() public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        __Pausable_init();
        __UUPSUpgradeable_init();
    }

    // Owner functions ==============================================

    /**
     * @notice Authorize/deauthorize a relayer
     * @param relayer Relayer address
     * @param authorized True to authorize, false to deauthorize
     */
    function setRelayerAuthorization(address relayer, bool authorized) external onlyOwner {
        require(relayer != address(0), "Invalid relayer address");
        authorizedRelayers[relayer] = authorized;
        emit RelayerAuthorized(relayer, authorized);
    }

    // Pause management functions ================================
    /**
     * @notice Pause the contract with a reason
     * @param reason Reason for pausing the contract
     */
    function pauseWithReason(string calldata reason) external onlyOwner {
        require(!paused(), "Already paused");
        require(bytes(reason).length > 0, "Pause reason required");
        
        pauseState = PauseInfo({
            pausedAt: block.timestamp,
            pauseReason: reason
        });
        
        _pause();
        emit PausedWithReason(reason);
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        require(paused(), "Not paused");
        _unpause();
    }

    

    // Meta-transaction functions ================================

    /**
     * @notice Execute a meta-transaction on behalf of a user (internal use only)
     * @param from User's address
     * @param metaTx Meta-transaction data
     * @return success True if the transaction was successful
     */
    function _executeMetaTransaction(
        address from,
        MetaTransaction memory metaTx
    ) internal returns (bool success) {
        // Execute the transaction with try-catch to handle failures gracefully
        try this._safeExecuteCall(metaTx.to, metaTx.value, metaTx.data) returns (bool _success) {
            success = _success;
        } catch {
            success = false;
        }
        
        emit MetaTransactionExecuted(msg.sender, from, metaTx.to, metaTx.value, metaTx.data, success);
        
        return success;
    }

    /**
     * @notice Helper function to safely execute external calls
     * @dev This function is external to allow try-catch usage
     */
    function _safeExecuteCall(address target, uint256 value, bytes calldata data) external returns (bool success) {
        require(msg.sender == address(this), "Only self-calls allowed");
        (success, ) = target.call{value: value}(data);
        return success;
    }

    /**
     * @notice Calculate total native token value required for batch transactions
     * @param metaTxs Array of meta-transactions
     * @return totalValue Total native token value needed
     */
    function _calculateTotalValue(MetaTransaction[] calldata metaTxs) internal pure returns (uint256 totalValue) {
        for (uint256 i = 0; i < metaTxs.length; i++) {
            totalValue += metaTxs[i].value;
        }
        return totalValue;
    }

    /**
     * @notice Batch execute multiple meta-transactions
     * @param from User's address
     * @param metaTxs Array of meta-transactions to execute
     * @param signature signature corresponding to entire meta transaction
     * @param nonce User's nonce
     * @param deadline Transaction deadline
     * @return successes Array of success status for each transaction
     */
    function executeMetaTransactions(
        address from,
        MetaTransaction[] calldata metaTxs,
        bytes calldata signature,
        uint256 nonce,
        uint256 deadline
    ) external payable nonReentrant whenNotPaused returns (bool[] memory successes) {
        require(authorizedRelayers[msg.sender], "Unauthorized relayer");
        require(block.timestamp <= deadline, "Transaction expired");
        require(nonce == nonces[from], "Invalid nonce");
        require(_verifySignature(from, metaTxs, signature, nonce, deadline), "Invalid signature");

        require(metaTxs.length > 0, "Empty batch Txs");

        // Calculate total value required for all meta-transactions
        uint256 totalValueRequired = 0;
        if (msg.value > 0) {
            totalValueRequired = _calculateTotalValue(metaTxs);
            require(msg.value == totalValueRequired, "Incorrect native token amount");
        }

        successes = new bool[](metaTxs.length);

        // Store batch transaction log
        uint256 batchId = nextBatchId++;
        uint256 valueUsed = 0;

        // Execute all transactions in the batch
        for (uint256 i = 0; i < metaTxs.length; i++) {
            bool success = _executeMetaTransaction(from, metaTxs[i]);
            
            // Track value used for each transaction
            if (success) {
                valueUsed += metaTxs[i].value;
            }
        }

        // Refund unused native tokens if any transactions failed
        if (totalValueRequired > 0) {
            uint256 refundAmount = totalValueRequired - valueUsed;
            if (refundAmount > 0) {
                (bool refundSuccess, ) = payable(from).call{value: refundAmount}("");
                require(refundSuccess, "Refund failed");
            }

            emit NativeTokenUsed(batchId, totalValueRequired, valueUsed, refundAmount);
        }
        
        // Increment nonce to prevent replay
        nonces[from]++;        

        return successes;
    }

    // Helper functions ==========================================

    /**
     * @notice Verify EIP-712 signature for batch meta-transactions
     * @param from User's address
     * @param metaTxs Array of meta-transactions
     * @param signature User's signature
     * @param nonce User's nonce
     * @param deadline User's deadline
     * @return valid True if signature is valid
     */
    function _verifySignature(
        address from,
        MetaTransaction[] calldata metaTxs,
        bytes calldata signature,
        uint256 nonce,
        uint256 deadline
    ) internal view returns (bool valid) {
        bytes32 domainSeparator = _buildDomainSeparator();
        
        // Hash each MetaTransaction struct according to EIP-712
        bytes32[] memory metaTxHashes = new bytes32[](metaTxs.length);
        for (uint256 i = 0; i < metaTxs.length; i++) {
            metaTxHashes[i] = keccak256(abi.encode(
                META_TRANSACTION_STRUCT_TYPEHASH,
                metaTxs[i].to,
                metaTxs[i].value,
                keccak256(metaTxs[i].data)
            ));
        }

        // Create the main struct hash
        bytes32 structHash = keccak256(abi.encode(
            META_TRANSACTION_TYPEHASH,
            from,
            keccak256(abi.encodePacked(metaTxHashes)),
            nonce,
            deadline
        ));

        // Create the final digest according to EIP-712
        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            domainSeparator,
            structHash
        ));

        // Recover signer and verify
        address recoveredSigner = digest.recover(signature);
        return recoveredSigner == from;
    }

    /**
     * @notice Build domain separator for EIP-712
     * @return domainSeparator The domain separator hash
     */
    function _buildDomainSeparator() internal view returns (bytes32 domainSeparator) {
        return keccak256(abi.encode(
            EIP712_DOMAIN_TYPEHASH,
            keccak256(bytes("MetaTxGateway")),
            keccak256(bytes("1")),
            block.chainid,
            address(this)
        ));
    }

    // View functions ============================================

    /**
     * @notice Get total native token value required for meta-transactions (external view)
     * @param metaTxs Array of meta-transactions
     * @return totalValue Total native token value needed
     */
    function calculateRequiredValue(MetaTransaction[] calldata metaTxs) external pure returns (uint256 totalValue) {
        return _calculateTotalValue(metaTxs);
    }

    /**
     * @notice Get the current nonce for a user
     * @param user User address
     * @return currentNonce Current nonce value
     */
    function getNonce(address user) external view returns (uint256 currentNonce) {
        return nonces[user];
    }

    /**
     * @notice Check if a relayer is authorized
     * @param relayer Relayer address
     * @return isAuthorized True if relayer is authorized
     */
    function isRelayerAuthorized(address relayer) external view returns (bool isAuthorized) {
        return authorizedRelayers[relayer];
    }

    /**
     * @notice Get the domain separator for EIP-712
     * @return separator Domain separator hash
     */
    function getDomainSeparator() external view returns (bytes32 separator) {
        return _buildDomainSeparator();
    }

    /**
     * @notice Get the MetaTransaction struct typehash for EIP-712
     * @return typehash The MetaTransaction struct typehash
     */
    function getMetaTransactionTypehash() external pure returns (bytes32 typehash) {
        return META_TRANSACTION_STRUCT_TYPEHASH;
    }

    /**
     * @notice Get the main typehash for batch meta-transactions
     * @return typehash The main typehash for EIP-712
     */
    function getMainTypehash() external pure returns (bytes32 typehash) {
        return META_TRANSACTION_TYPEHASH;
    }

    /**
     * @notice Generate the EIP-712 digest for signing (helper for frontend)
     * @param from User's address
     * @param metaTxs Array of meta-transactions
     * @param nonce User's nonce
     * @param deadline Transaction deadline
     * @return digest The digest to be signed
     */
    function getSigningDigest(
        address from,
        MetaTransaction[] calldata metaTxs,
        uint256 nonce,
        uint256 deadline
    ) external view returns (bytes32 digest) {
        bytes32 domainSeparator = _buildDomainSeparator();
        
        // Hash each MetaTransaction struct according to EIP-712
        bytes32[] memory metaTxHashes = new bytes32[](metaTxs.length);
        for (uint256 i = 0; i < metaTxs.length; i++) {
            metaTxHashes[i] = keccak256(abi.encode(
                META_TRANSACTION_STRUCT_TYPEHASH,
                metaTxs[i].to,
                metaTxs[i].value,
                keccak256(metaTxs[i].data)
            ));
        }

        // Create the main struct hash
        bytes32 structHash = keccak256(abi.encode(
            META_TRANSACTION_TYPEHASH,
            from,
            keccak256(abi.encodePacked(metaTxHashes)),
            nonce,
            deadline
        ));

        // Create the final digest according to EIP-712
        return keccak256(abi.encodePacked(
            "\x19\x01",
            domainSeparator,
            structHash
        ));
    }

    /**
     * @notice Get total number of batch transactions processed
     * @return count Total batch transaction count
     */
    function getTotalBatchCount() external view returns (uint256 count) {
        return nextBatchId;
    }

    /**
     * @notice Get the contract version
     * @return version The version string for this contract
     */
    function getVersion() external pure returns (string memory version) {
        return "v1.0.0-native-token-support";
    }

    // Upgrade authorization =====================================

    /**
     * @dev Authorizes contract upgrades (UUPS pattern)
     * @param newImplementation The address of the new implementation
     * @notice Only owner can authorize upgrades
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

